#[
    Simple addition function

import strutils

proc Addition(a: int, b: int): int =
    result = a + b

echo "Enter a number:"
let A = parseInt(stdin.readLine)
echo "Enter another number:"
let B = parseInt(stdin.readLine)

echo "The sum of these two numbers is " & $Addition(A,B)
]#

#[ 
    Simple minutes -> seconds conversion
import strutils

proc ConvertSeconds(min: int): int =
    result = min * 60

echo "Enter the number of minutes."
let mins = parseInt(stdin.readLine)
let secs = ConvertSeconds(mins)

echo "Equivalent number of seconds: " & $secs
]#

#[ 
    
import strutils

proc PosOrNeg(num: int): string =
    result = case num:
        of low(int) .. -1:
            "negative"
        of 1 .. high(int):
            "positive"
        of 0:
            "equal"

echo "Enter a number:"
let num = parseInt(stdin.readLine)
let r = PosOrNeg(num)
if r == "equal":
    echo "This number is 0!"
else:
    echo "This number is " & r & "."
]#
